import React from "react";
import './App.css';

function Todo ( { todo } ){
  return(
    <div className="todo">
      {todo.text}
    </div>
  );
}


function TodoForm ( { todo } ){
  return(
    <form>
      <input 
        type='text'
        placeholder="Todos..."
        className="input"
      />
    </form>
  );
}


function App () {
  const [ todos, setTodos ] = React.useState([
    {
      text: 'React'
    },
    {
      text: 'React'
    },
    {
      text: 'React'
    },
  ])


  return(
    <div className="app">
      <h1>Todo</h1>
      <div className="todo-list">
        {todos.map(( todo, index ) => (
          <Todo 
            index={index}
            key={index}
            todo={todo}
          />
        ))}
      </div>
    </div>
  );
}


export default App;
