import React from 'react'
import './App.css';


function Todo ( { todo } ) {
  return(
    <div className='todo'>
      {todo.text}
    </div>
  );
}

function TodoForm ( { addTodo } ) {
  const [ value, setValue ] = React.useState('');

  const handleSubmit = event => {
    event.preventDefault();
    if (!value) return;
    addTodo(value);
    setValue('');
  }

  return(
    <form onSubmit={handleSubmit}>
      <input 
        placeholder='Todos'
        className='input'
        type='text'
        value={value}
        onChange={ event => setValue(event.target.value)}
      />
    </form>
  );
}


function App(){
  const [ todos, setTodos] = React.useState([
    {
      text: 'React',
    },
    {
      text: "Vadim",
    }
  ]);

  const addTodo = text => {
    const newTodos = [...todos, { text }];
    setTodos(newTodos)
  }

  return(
    <div className='app'>
      <h1>Todos</h1>
      <div className='todo-list'>
        {todos.map((todo, index) => (
          <Todo 
            key={index}
            index={index}
            todo={todo}

          />
        ))}
        <TodoForm addTodo={addTodo} />
      </div>
    </div>
  );

}

export default App
