import React from 'react';
import './App.css';

function Todo ( { todo } ) {
  return(
    <div className='todo'>
      {todo.text}
    </div>
  );
}

function TodoForm () {
  const [ value, setValue ] = React.useState('');

  const handlSubmit = event => {
    event.preventDefault();
    if (!value) return;
    setValue('');
  }

  return (
    <form>
      <input 
        type='text'
        className='input'
        value={value}
        onChange={ e => setValue(e.target.value)}
        placeholder='Todos' 
      />
    </form>
  );
}

function App () {
  const [ todos, setTodos ] = React.useState([
    {
      text: 'React',
    },
    {
      text: 'Vadim',
    }
  ]);

  return(
    <div className='app'>
      <h1>Todos</h1>
      <div className='todo-list'>
        {todos.map((todo, index) => (
          <Todo 
            key={index}
            index={index}
            todo={todo}

          />
        ))}
        <TodoForm  />
      </div>
    </div>
  );

}

export default App
